function getFiles(params){
    var param={
        Bucket:params.SrcBucket,
        Key:params.Key
    }
    console.log("get param",param)
    return s3.getObject(param).promise()
    .get("Body")
    .then(function(buff){
        return jszip.loadAsync(buff)
        .get('files')
        .then(function(files){
            return Object.keys(files)
                .map(key=>files[key])
                .filter(file=>!file.dir)
                .map(file=>file.name)
        })
        .tap(console.log)
    })
}

