CONDA=/home/ec2-user/anaconda3/bin/conda
NODE=/home/ec2-user/anaconda3/envs/JupyterSystemEnv/bin/node
TMP=/home/ec2-user/tmp

A=$($CONDA list --json | jq  '.' --raw-output -c)

